INSERT INTO public.user_professions (user_id, profession_id, description, experience_years) VALUES
('1d8bbf2c-2dfb-4325-986c-2a9ca97e0202', '4e7aa094-fe62-4abc-889c-76d03bb176d9', 'Sarah rend la lecture des actes notariés aussi passionnante qu''un roman policier.', 1),
('143698eb-5e38-4454-bb32-c5cd68e85d89', 'eae10000-8d0d-4e9e-8085-86248f0d9db2', 'J''apporte toujours une touche de bonne humeur dans ses soins, même lors des prises de sang !', 1),
('75c5259b-2d1e-4fbf-88db-ee2d6b0271ec', '7ad8d99f-5b1e-4e71-8001-0aa90d140bf5', 'Avec Sophie, chaque plaidoirie devient un spectacle. Elle défend ses clients avec panache et humour.', 10),
('02003d99-069f-4478-848a-0efcc1a24f39', '0266baba-1483-48a6-940d-8ed04957670e', 'le mécanicien qui parle aux voitures, rend chaque réparation ludique.', 10),
('af41cbec-068a-4dd2-806c-664272db776a', '86e524b6-1fbb-40cb-b5b4-0dfd1934b187', 'Maxime compose des chansons amusantes pour enseigner la musique, ses cours sont de véritables concerts.', 2),
('c2cef04a-2398-4609-8d44-e2e76acf8b61', '08e926c9-96db-4ad1-ac8f-f9fe2f5b28ef', 'Jardinier passionné, avec un talent naturel pour transformer les espaces verts en véritables œuvres d''art. Expert en plantes, il partage volontiers des astuces de jardinage dans une ambiance légère et conviviale. Reconnu pour sa main verte et son humour fleuri, il apporte vie et couleur dans chaque jardin qu''il touche.', 9),
('f99df2b1-ab71-4376-af48-818a32995c28', '0543e471-8e69-4fc2-8eb8-d62187fa4f67', 'la codeuse aux mille et un easter eggs, ajoute une touche ludique à chaque site web.', 4),
('75daaeab-d287-4cec-a66c-aad5673d3883', 'bc4c99ee-892d-4cbc-ae2a-049e8e6b97bd', 'la coiffeuse aux ciseaux magiques, transforme chaque coupe en spectacle.', 3),
('7f4528d1-161c-4a86-9463-33ca667c71b6', '6f18b006-b3eb-485c-8520-a16b71ab5255', 'chaque coup de pinceau est accompagné d''une anecdote colorée.', 7),
('369a3a7f-82ac-4de6-8ea6-1ee42878a1f1', '4d1569b6-b7cf-4a15-a6b4-655c56286193', 'expert en sauvetage numérique, raconte des histoires de ''cyber-héros''.', 2),
('44e30ff9-3731-4ffb-b610-644acb58deab', '3d9b1808-a86f-4b63-9426-aad4c2f183dc', 'Expert-comptable chevronné, reconnu pour son habileté à naviguer dans les complexités fiscales et comptables. Allie expertise technique et un sens de l''humour inattendu, transformant les chiffres en histoires captivantes. Dévoué à fournir des solutions claires et précises, assurant tranquillité d''esprit et fiabilité à ses clients.', 6),
('afeba8ba-f00a-4288-bdbe-d42b99b39268', '1e1ac892-7615-433e-998a-bac1c39d906b', 'le styliste qui habille les rêves, fait de chaque tenue une aventure.', 2),
('c57a6ddd-2a43-41c9-ac90-a2e99f4b37d1', '7f595e8a-a02a-4885-93a3-631a06359443', ' le maçon blagueur, construit des murs et de bonnes histoires en même temps.', 3),
('129b4fbd-654c-4c5a-861c-fed6986cf435', 'f64041d4-4e19-46bb-9344-5ab693a79745', 'Thomas transforme chaque leçon en aventure, ses élèves l''attendent toujours avec impatience.', 4),
('a6f151a7-d3dd-4b66-b6dd-eb1c66f8df31', '23ec70eb-b449-455a-8638-7cd209ac2bd3', 'le chef pâtissier qui raconte des histoires sucrées, transforme chaque dessert en conte de fées.', 10),
('fd5d76e2-9958-409c-92a6-55a49199fd72', '0543e471-8e69-4fc2-8eb8-d62187fa4f67', 'le meilleur de la région paca !', 10),
('224da087-3c01-4ed2-8215-12d1e2487544', 'ba989c47-c963-45d7-a0d0-9b6f989d364f', 'Médecin généraliste expérimenté, dédié à fournir des soins de santé complets et personnalisés. Expert en diagnostic et en traitement de diverses affections, avec un engagement fort envers la médecine préventive et le bien-être global des patients. Approche empathique et à l''écoute, favorisant une relation de confiance et de collaboration avec chaque patient.', 10),
('1d77b90d-a24d-40f3-a8d5-d38e157107d4', 'e3f298ef-5d3e-4daf-b2f9-ab6b21e5f068', 'Passionnée par les sourires éclatants, Émilie aime raconter des blagues pour détendre ses patients.', 7),
('0c2b4f5d-e514-4429-bf68-c33aa9f97151', '6380d547-5466-4632-bcbd-f2c23b5b9bfa', 'Lucas, toujours en tenue de sport, est connu pour transformer les séances d''entraînement en véritables fêtes fitness', 5),
('a009b612-ea64-44fa-9789-a89794530c14', '8e192643-4585-4ed4-ac12-da044a50c924', 'le vétérinaire qui murmure à l''oreille des animaux, partage des anecdotes amusantes de sa pratique quotidienne.', 3),
('927d0e27-65e5-4e65-898f-b569e2e7dfe8', '0f3e75a3-7d82-4a92-ba37-ca4b24178feb', 'le plombier qui chante toujours en travaillant, rend chaque réparation mémorable.', 10),
('2e3b783f-b85f-4733-8b4c-32f26c7be18e', 'be551d90-2974-41be-b1be-ec8bba42201b', 'l''artiste des ongles, raconte des contes colorés à chaque manucure.', 4),
('8c3f7542-8c8d-429c-9b30-88617dddabb8', '162c8a32-8a93-49bd-8b3e-dca5b5c0eafb', 'Agent immobilier dynamique et attentif, spécialiste en dénicher des perles rares sur le marché. Connaisseur des tendances locales, il offre des conseils personnalisés pour chaque client. Réputé pour son humour et son approche amicale, rendant chaque visite de propriété une expérience unique et mémorable.', 6),
('c6e75680-a6d3-4068-b8a9-ef5f85684133', '57b8acf3-9227-459f-9041-70dd3827b841', 'la gardienne d''animaux avec un humour animalier irrésistible, rend chaque garde une aventure.', 10),
('c1c2eac1-f86f-49d3-b34f-ae0fb217753b', 'cb204ab5-3cd6-4b22-850c-44d79e8c9162', 'Chauffeur de taxi expérimenté, connu pour sa ponctualité et sa connaissance approfondie de la ville. Toujours prêt à partager une anecdote locale ou une blague pour égayer le trajet. Engagé à offrir un service de qualité et une conduite sécuritaire pour une expérience client agréable.', 9);
